import React from 'react'
import { cn } from '@/lib'
import {
  DuckTableContext,
  DuckTableProps,
  DuckTableProviderProps,
  TableSearchStateType,
} from './table-advanced.types'
import { Table } from './table'
import { ScrollArea, ScrollBar } from '@/registry/default/ui/scroll-area'

export const DuckTableProvider = <TColumnName extends string[]>({
  table_data,
  children,
  className,
  ...props
}: DuckTableProviderProps<TColumnName>) => {
  const [search, setSearch] = React.useState<TableSearchStateType>({
    query: '',
    queryBy: [],
  })

  return (
    <DuckTableContext.Provider
      value={{
        table_data,
        search,
        setSearch,
      }}
    >
      <div
        className={cn(
          `w-full- flex flex-col gap-4 w-[900px] h-[500px]`,
          className,
        )}
        {...props}
      >
        {children}
      </div>
    </DuckTableContext.Provider>
  )
}

export const DuckTable = ({
  wrapper,
  className,
  children,
  ...props
}: DuckTableProps) => {
  const { className: wrapperClassName, ...wrapperProps } = wrapper! ?? {}

  return (
    <ScrollArea
      className={cn(
        'border border-border rounded-lg !overflow-visible relative',
        wrapperClassName,
      )}
      {...wrapperProps}
    >
      <Table {...props}>{children}</Table>
      <ScrollBar orientation="horizontal" />
    </ScrollArea>
  )
}

DuckTable.displayName = 'DuckTable'
